// GOOGLE BOOKS API KEY
const APIkey = "AIzaSyArI9xPpFOe-eMvnp1FVwwJOBuJ_bihgo0";

export default {
    // THIS METHOD GETS ALL IDS FROM BOOKS WITH MATCHING TITLES
    getBookIds: (search) => {
        return axios.get("https://www.googleapis.com/books/v1/volumes?q=intitle:" + search + "&key=" + APIkey)
    },

    // THIS METHOD GETS ALL BOOK INFO BASED ON THE IDS GATHERED ABOVE
    getBookInfo: (volumeIds) => {
        return axios.get("https://www.googleapis.com/books/v1/volumes/" + volumeIds + "?key=" + APIkey)
    }
}








